import React, { Component } from "react";
import StudentSearvice from "../Services/StudentSearvice";

class CreateStudentComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // step 2
      id: this.props.match.params.id,
      Name: "",
      Age: "",
    };
    this.changeNameHandler = this.changeNameHandler.bind(this);
    this.changeAgeHandler = this.changeAgeHandler.bind(this);
    this.saveOrUpdateStudent = this.saveOrUpdateStudent.bind(this);
  }
 //step 3
  componentDidMount(){

    // step 4
    if(this.state.id === '_add'){
        return
    }else{
        StudentSearvice.getStudentById(this.state.id).then( (res) =>{
            let student = res.data;
            this.setState({name: student.Name,
                age: student.Age
            });
        });
    }        
}


  saveOrUpdateStudent = (s) => {
    s.preventDefault();
    let student = {
      name: this.state.Name,
      age: this.state.Age,
    };
    console.log("student => " + JSON.stringify(student));

    //step 5
    if (this.state.id === "_add") {
      StudentSearvice.createStudent(student).then((res) => {
        this.props.history.push("/students");
      });
    } else {
      StudentSearvice.updateEmployee(student, this.state.id).then((res) => {
        this.props.history.push("/students");
      });
    }
  };

  changeNameHandler = (event) => {
    this.setState({ Name: event.target.value });
  };

  changeAgeHandler = (event) => {
    this.setState({ Age: event.target.value });
  };
  cancel() {
    this.props.history.push("/students");
  }

  getTitle() {
    if (this.state.id === "_add") {
      return <h3 className="text-center">Add Student</h3>;
    } else {
      return <h3 className="text-center">Update Student</h3>;
    }
  }
  render() {
    return (
      <div>
        <br></br>
        <div className="container">
          <div className="row">
            <div className="card col-md-6 offset-md-3 offset-md-3">
              {this.getTitle()}
              <div className="card-body">
                <form>
                  <div className="form-group">
                    <label> Name: </label>
                    <input
                      placeholder="Insert your name here"
                      name="name"
                      className="form-control"
                      value={this.state.Name}
                      onChange={this.changeNameHandler}
                    />
                  </div>
                  <div className="form-group">
                    <label> Age: </label>
                    <input
                      placeholder="Insert your name here"
                      name="age"
                      className="form-control"
                      value={this.state.Age}
                      onChange={this.changeAgeHandler}
                    />
                  </div>
                  <button
                    className="btn btn-success"
                    onClick={this.saveOrUpdateStudent}
                  >
                    Save
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={this.cancel.bind(this)}
                    style={{ marginLeft: "10px" }}
                  >
                    Cancel
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateStudentComponent;
