import React, { Component } from 'react'
import StudentSearvice from '../Services/StudentSearvice';

export default class ViewStudentComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            student: {}
        }
    }


    componentDidMount(){
        StudentSearvice.getStudentById(this.state.id).then( res => {
            this.setState({student: res.data});
        })
    }
    
    render() {
        return (
            <div>
            <br></br>
            <div className = "card col-md-6 offset-md-3">
                <h3 className = "text-center"> View Employee Details</h3>
                <div className = "card-body">

                    <div className = "row">
                        <label> Student's Name: </label>
                        <div> { this.state.student.name }</div>
                    </div>

                    <div className = "row">
                        <label> Age: </label>
                        <div> { this.state.student.age }</div>
                    </div>

                </div>

            </div>
        </div>
        )
    }
}
