import logo from "../logo.svg";
import React, { Component } from "react";
import { withRouter } from "react-router";


class HeaderComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      Student:[]
    };
  }

  goEmployees() {
    this.props.history.push("/students");
  }

  goHome() {
    this.props.history.push("/Home");
  }

  addStudent() {
    this.props.history.push("/add-student/_add");
  }

  
  render() {
    return (
      <div>
        <header className="bg-black">
            <h1 className=" text-info">
              <img
                src={logo}
                className="App-logo"
                alt="logo"
              />
              Employee Management System
            </h1>
            <button className="btn btn-primary float-Right" style={{marginLeft: "10px"}} onClick={this.addStudent.bind(this)}>
            Add Employee
            </button>
            <button className="btn btn-primary float-Right" style={{marginLeft: "10px"}} onClick={this.goEmployees.bind(this)}>
            Our Employees
            </button>
            <button className="btn btn-primary float-Right" style={{marginLeft: "10px"}} onClick={this.goHome.bind(this)}>
            Home
            </button>
         

          </header>
      </div>
    );
  }
}
export default  withRouter(HeaderComponent);
